/*Table structure for table `cr_sample` */

DROP TABLE IF EXISTS `cr_sample`;

CREATE TABLE `cr_sample` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '状态',
  `sam_name` varchar(30) DEFAULT NULL COMMENT '名称',
  `trade` varchar(60) DEFAULT NULL COMMENT '行业',
  `amount` double DEFAULT NULL COMMENT '金额',
  `sam_type` smallint(3) DEFAULT NULL COMMENT '类型(1案例、2用户心声)',
  `sam_img` varchar(300) DEFAULT NULL COMMENT '图片路径',
  `description` varchar(600) DEFAULT NULL COMMENT '描述',
  `status` smallint(6) DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

