mapper.select:
	select count(1)
	from cr_sample u
	where 1=1

		and u.sam_type = #{samType}


  		and	CONCAT(IFNULL(u.sam_name,''),IFNULL(u.trade,'')) like #{keyword}


	from cr_sample u
	where u.status = 1

		and u.sam_type = #{samType}


  		and	CONCAT(IFNULL(u.sam_name,''),IFNULL(u.trade,'')) like #{keyword}

	order by u.id asc

  		limit #{from},#{size}
